import './categories.styles.scss';

const App = () => {

  const categories = [
    {
      id: 1,
      title: 'Hats',
      imageURL: '' 
    },
    {
      id: 2,
      title: 'Jackets',
      imageURL: '' 
    },
    {
      id: 3,
      title: 'Sneakers',
      imageURL: '' 
    },
    {
      id: 4,
      title: 'Womans',
      imageURL: 'https://www.staff-clothes.com/static/images/n-girls.jpg' 
    },
    {
      id: 5,
      title: 'Mans',
      imageURL: 'https://www.staff-clothes.com/static/images/n-boys.jpg' 
    }
  ]

  return (
    <div className="categories-container">
      {categories.map(({title, id, imageURL}) => (
        <div key={id} className="category-container">
        <div className='background-image' style={{
          backgroundImage: `url{${imageURL}}`
        }} />
        <div className="category-body-container">
          <h2>{title}</h2>
          <p>Shop now</p>
        </div>
      </div>
      ))}
    </div>
  );
}

export default App;
